	import { onMount } from 'svelte';
	import { animate, scroll, ScrollOffset } from 'motion';

	let el_trigger1: Element;
	let el_path1: Element;
	let el_people_circle: Element;
	let is_part1_finished = false;
	const triggerPart1Change = () => {
		if (is_part1_finished) {
			animate(el_people_circle, { opacity: [0, 1] });
		} else {
			animate(el_people_circle, { opacity: [1, 0] });
		}
	};

	let el_trigger2: Element;
	let el_path2: Element;
	let el_party_circle: Element;
